
class CelebrationModal {
    constructor() {
        this.modal = document.createElement('div');
        this.modal.className = 'celebration-modal hidden';
        
        this.modal.innerHTML = `
            <h2>Congratulations!</h2>
            <p>You've achieved something amazing!</p>
            <button>Close</button>
        `;

        document.body.appendChild(this.modal);

        this.modal.querySelector('button').addEventListener('click', () => {
            this.hide();
        });
    }

    show() {
        this.modal.classList.remove('hidden');
    }

    hide() {
        this.modal.classList.add('hidden');
    }

    setMessage(title, message) {
        this.modal.querySelector('h2').textContent = title;
        this.modal.querySelector('p').textContent = message;
    }
}
